    }

    render() {
        return(
            <div className="Container">
                <form onSubmit={() => this.addHandler} className="AddBox">
                    <input type='text' value={this.state.input} onChange={() =>inputHandler}/>
                    <input type='submit'/>
                </form>
                {this.state.task.map((el)=>(
                    <form className="CheckBox" key={el.id}>
                        <input type="checkbox" checked={el.check} onChange={()=>this.checkHandler(el.id)}/>
                        <p className={`task ${el.check ? "task-checked" : ""}`}>{el.name}</p>
                        <input type="button" value="delete" onClick={()=>this.deleteHandler(el.id)}/>
                    </form>
                ))}
            </div>
        )
    }
} 
export default CheckBox;
